import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Modal from '../components/Modal';

function Assets() {
  const [assets, setAssets] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [form, setForm] = useState({
    value: 0,
    quantity: 0,
    code: '',
    expectedReturn: 0
  });

  useEffect(() => {
    axios.get('http://localhost:3001/assets')
      .then(res => setAssets(res.data));
  }, []);

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = () => {
    axios.post('http://localhost:3001/assets', form)
      .then(res => setAssets([...assets, res.data]));
    setShowModal(false);
  };

  return (
    <div>
      <h2>Gestión de Activos</h2>
      <button onClick={() => setShowModal(true)}>+ Agregar Activo</button>
      <ul>
        {assets.map((a, i) => (
          <li key={i}>{a.code} - {a.quantity} x ${a.value} ({a.expectedReturn}%)</li>
        ))}
      </ul>
      {showModal && (
        <Modal onClose={() => setShowModal(false)}>
          <input name="value" type="number" placeholder="Valor" onChange={handleChange} />
          <input name="quantity" type="number" placeholder="Cantidad" onChange={handleChange} />
          <input name="code" placeholder="Código" onChange={handleChange} />
          <input name="expectedReturn" type="number" placeholder="% Esperado" onChange={handleChange} />
          <button onClick={handleSubmit}>Guardar</button>
        </Modal>
      )}
    </div>
  );
}

export default Assets;
