import React, { useState } from 'react';
import axios from 'axios';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend } from 'recharts';

function Simulate() {
  const [form, setForm] = useState({
    initialAmount: 1000,
    monthlyContribution: 100,
    rate: 2,
    months: 12,
  });

  const [data, setData] = useState([]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: Number(e.target.value) });
  };

  const simulate = () => {
    axios.post('http://localhost:3001/simulate', form)
      .then(res => setData(res.data))
      .catch(err => console.error(err));
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>Simulador de Inversión</h2>
      <input type="number" name="initialAmount" placeholder="Monto Inicial" value={form.initialAmount} onChange={handleChange} />
      <input type="number" name="monthlyContribution" placeholder="Aporte Mensual" value={form.monthlyContribution} onChange={handleChange} />
      <input type="number" name="rate" placeholder="% Ganancia" value={form.rate} onChange={handleChange} />
      <input type="number" name="months" placeholder="Duración (meses)" value={form.months} onChange={handleChange} />
      <button onClick={simulate}>Simular</button>

      {data.length > 0 && (
        <LineChart width={600} height={300} data={data}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="month" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Line type="monotone" dataKey="value" stroke="#8884d8" />
        </LineChart>
      )}
    </div>
  );
}

export default Simulate;
